sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if (newValue == 'true'){
		g_form.setVisible('u_rejection_reason', true);
		g_form.setMandatory('u_rejection_reason', true);
		g_form.showFieldMsg('u_rejection_reason','This infomation will be emailed to the requestor and approver','error',true);

		//Hide client Reject ui action
		var items = $$('BUTTON').each(function(item){
			if (item.innerHTML.indexOf('Reject Approval Biz') > -1) {
				item.hide();
			}
		});

   }

		//$j('[data-action-name=prepReject]').hide();
		//$$('prepReject')[0].hide();
		//var items = $$('prepReject').each(function(item){
			//if(item.innerHTML.indexOf('Reject Approval Client') > -1){
				//item.hide();
			//}
		//});


}
