sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  if (isLoading || newValue == '') {
    return;
  }
	//Grabs values for todays date and selected date
	var startDate = new Date();
	startDate = new Date(startDate.toLocaleDateString());
	var date = new Date(newValue);

	var prio = g_form.getValue('u_priority');

	//maths then converts it from miliseconds
	var diff = Math.floor((date - startDate) / (1000 * 60 * 60 * 24));


	if (diff < 13 && prio != 1){
		var projTitle = g_form.getValue('u_project_title');
		g_form.showFieldMsg('u_requested_draft','Need to be 14 days atleast','error');
		g_form.flash("x_otte2_creative_f_creative_requests.u_project_title", "#FF0000", 0);
		//g_form.clearValue("u_project_title");
		g_form.setValue("u_project_title", "Please fix draft review date");
		g_form.setReadOnly("u_project_title", true);

	} else if (g_form.getValue("u_project_title") == "Please fix draft review date") {
		g_form.setReadOnly("u_project_title", false);
		g_form.clearValue("u_project_title");
	}
}
