sys_script_client.script:
function onLoad() {

	//Hide reject checkbox
	//g_form.setVisible('u_rejected', false);
	//Reject reason string
	/* if(g_form.getValue('u_rejection_reason') != ''){
		g_form.setVisible('u_rejection_reason', true);
		g_form.setMandatory('u_rejection_reason', false);
	}else {
		g_form.setVisible('u_rejection_reason', false);
	} */
	g_form.setVisible('u_rejection_reason', false);
	//Approval history
	if(g_user.hasRole('creative_super_user')){
		g_form.setVisible('u_approval_history', true);
	}else {
		g_form.setVisible('u_approval_history', false);
	}

	//Rejection
	g_form.setVisible('u_rejected', false);

	//sysapproval
	g_form.setVisible('u_sysapproval', false);

	//active
	g_form.setVisible('active', false);

	//Submitted
	g_form.setVisible('u_locked', false);

	//for before query business rule
	g_form.setVisible('u_problem_report', false);
	g_form.setValue('u_problem_report', true);

	//Send Approval
	g_form.setVisible('u_send_approval', false);

	//MOPS@otterproducs.com for flow
	g_form.setVisible('u_mops', false);

	//completed
	if (!g_user.hasRole('creative_super_user')){
		g_form.setVisible('u_completed', false);
	}
}
