sys_ui_policy.script_true:
function onCondition() {
	//Checks if super user, unlocks all fields
	if(g_user.hasRole('creative_super_user')) {
		var fields = g_form.getEditableFields();
		for (var x = 0; x < fields.length; x++) {
			g_form.setReadOnly(fields[x], false);
	}
	}
	//Locks Dates after Submit
	else if (g_form.getValue('u_submitted') == 'true'){
		g_form.setReadOnly('u_requested_draft', true);
		g_form.setReadOnly('u_brief_type', true);
		g_form.setReadOnly('u_brand', true);
		g_form.setReadOnly('u_project_title', true);
		g_form.setReadOnly('u_priority', true);
		g_form.setReadOnly('u_brief_approval', true);
		g_form.setReadOnly('u_reason', true);
		g_form.setReadOnly('u_assets_due', true);
		g_form.setReadOnly('u_status', true);
	}
	//Locks all fields after submission
	else if (g_form.getValue('u_locked') == 'true'){
		var allFields = g_form.getEditableFields();
			for (var y = 0; y < allFields.length; y++) {
			g_form.setReadOnly(allFields[y], true);
			}
	}
}
